}

sp_widget.script:
(function() {
  data.table = input && input.table || $sp.getParameter('table') || 'sc_req_item';
  data.sys_id = input && input.sys_id || $sp.getParameter('sys_id') || '';

  if (input && input.action === 'process_excel') {
    var processor = new sn_impex.ExcelParser(); // 内建 Excel 解析器
    var decoded = GlideStringUtil.base64DecodeAsBytes(input.base64);
    var stream = new java.io.ByteArrayInputStream(decoded);
    var workbook = processor.parseExcel(stream);

    var rows = [];

    // 解析第一张表（sheet）
    var sheet = workbook.getSheetAt(0);
    var header = [];
    var rowIterator = sheet.rowIterator();

    // 遍历每一行
    while (rowIterator.hasNext()) {
      var row = rowIterator.next();
      var cellIterator = row.cellIterator();

      var rowData = {};
      var i = 0;

      while (cellIterator.hasNext()) {
        var cell = cellIterator.next();
        var value = cell.toString().trim();

        if (row.getRowNum() === 0) {
          // 第一行是标题
          header[i] = value.toLowerCase(); // 比如 'name' 或 'state'
        } else {
          // 数据行
          rowData[header[i]] = value;
        }
        i++;
      }

      if (row.getRowNum() > 0 && Object.keys(rowData).length > 0) {
        rows.push(rowData);
      }
    }

    data.rows = rows;
  }
})();
