sys_script_include.script:
var PDFGenerator = Class.create();
PDFGenerator.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
  createPDF: function() {
    var html = "<h2>Generate a PDF file in the Scripting Demo</h2>";
    var table = "sys_user";       
    var recordId = gs.getUserID();
    var filename = "ScriptingDemo";

    try {
      var pdfGen = new sn_pdfgeneratorutils.PDFGenerationAPI();
      var result = pdfGen.convertToPDF(html, table, recordId, filename);

      if (result.status === "success") {        
        return "/sys_attachment.do?sys_id=" + result.attachment_id;
      } else {        
        return null;
      }
    } catch (e) {
      gs.error("Exception: " + (e.message || e));
      return null;
    }
  },

  type: 'PDFGenerator'
});
